import React from "react";

function SortBookCards({ books }) {
  // Sort the books by their released date in descending order
  const sortedBooks = [...books].sort((a, b) => {
    // Assuming `released` is a string in the format YYYY-MM-DD
    return new Date(b.released) - new Date(a.released);
  });

  return (
    <div className="flex flex-wrap">
      {sortedBooks.map((book, index) => (
        <div className="book " key={book.id}>
          <h3>Book {index + 1}:</h3>
          <h2>{book.name}</h2>

          <div className="details">
            <p>üë®: {book.authors}</p>
            <p>üìñ: {book.numberOfPages} pages</p>
            <p>üèòÔ∏è: {book.country}</p>
            <p>‚è∞: {new Date(book.released).toDateString()}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default SortBookCards;
