import { GoStarFill } from "react-icons/go";
import React, { useState } from "react";

function FavoriteBook({ book, nr, removeFromFavorites, sortFavorites }) {
  const [isSorted, setIsSorted] = useState(false);
  const data = new Date(book.released).toDateString();

  const handleRemoveFromFavorites = () => {
    removeFromFavorites(book);
  };

  const handleSortFavorites = () => {
    // Toggle the sorting state
    setIsSorted(!isSorted);

    // Sort the favorites using the sorting function
    sortFavorites();
  };

  return (
    <div className="book">
      <h3>Favorite Book: {nr}</h3>
      <h2>{book.name}</h2>

      <div className="details">
        <p>üë®: {book.authors}</p>
        <p>üìñ: {book.numberOfPages} pages</p>
        <p>üèòÔ∏è: {book.country}</p>
        <p>‚è∞: {data}</p>
      </div>

      <button onClick={handleRemoveFromFavorites} className="star">
        <GoStarFill /> Remove from Favorites
      </button>

      {/* Toggle the sorting behavior in favorites */}
      <button onClick={handleSortFavorites} className="sort-button">
        {isSorted ? "Unsort Favorites" : "Sort Favorites"}
      </button>
    </div>
  );
}

export default FavoriteBook;
