import React, { useState } from "react";
import { GoStar, GoStarFill } from "react-icons/go";

function BookCard({ book, nr, favorites, setFavorites }) {
  const data = new Date(book.released).toDateString();

  // Use local state to manage the favorite status for this book
  const [isBookFavorite, setIsBookFavorite] = useState(
    favorites.some((favorite) => favorite.id === book.id)
  );

  const toggleFavorite = () => {
    if (isBookFavorite) {
      // If the book is already a favorite, remove it from the favorites list
      const updatedFavorites = favorites.filter(
        (favorite) => favorite.id !== book.id
      );
      setFavorites(updatedFavorites);
    } else {
      // If the book is not a favorite, add it to the favorites list
      setFavorites([...favorites, book]);
    }

    // Update the local state to reflect the new favorite status
    setIsBookFavorite(!isBookFavorite);
  };

  return (
    <div className="book ">
      <h3>
        Book: {nr}
        <button onClick={toggleFavorite}>
          {isBookFavorite ? <GoStarFill /> : <GoStar />}
        </button>
      </h3>
      <h2>{book.name}</h2>

      <div className="details">
        <p>üë®: {book.authors}</p>
        <p>üìñ: {book.numberOfPages} pages</p>
        <p>üèòÔ∏è: {book.country}</p>
        <p>‚è∞: {data}</p>
      </div>
    </div>
  );
}

export default BookCard;
