import React, { useState } from "react";
import BookCard from "./BookCard";
import axios from "axios";
import SortBookCards from "./SortBookCard";
import FavoriteBook from "./FavoriteBook";

function App() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [sortbutton, setSortButton] = useState(false);
  const [showSortComponent, setShowSortComponent] = useState(false);
  const [favorites, setFavorites] = useState([]);
  const [showFavorites, setShowFavorites] = useState(false);
  const [sortedBooks, setSortedBooks] = useState([]);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      setProgress(0);

      // Simulate a 3-second delay
      const duration = 3000;
      const startTime = Date.now();
      console.log("starttime", startTime);
      // Function to update progress
      const updateProgress = () => {
        const currentTime = Date.now();
        const elapsedTime = currentTime - startTime;
        const percentage = Math.min((elapsedTime / duration) * 100, 100); //exp.elapsedtime=1sec/3sec=33.33%
        setProgress(percentage);
      };

      // Start updating progress every 100 milliseconds    0.1sec
      const progressInterval = setInterval(updateProgress, 100);

      const response = await axios.get(
        "https://www.anapioficeandfire.com/api/books?pageSize=30"
      );

      // Clear the progress update interval when the request is complete
      clearInterval(progressInterval);

      console.log(response.data);
      setBooks(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
      // Set progress to 100% when data fetching is complete
      setProgress(100);
      setShowSortComponent(true);
      setSortButton(false); // Reset sortbutton to false when fetching new data
    }
  };

  const addToFavorites = (book) => {
    if (!favorites.some((favorite) => favorite.isbn === book.isbn)) {
      setFavorites([...favorites, book]);
    }
  };

  const removeFromFavorites = (index) => {
    setFavorites((prevFavorites) => {
      const updatedFavorites = [...prevFavorites];
      updatedFavorites.splice(index, 1);
      return updatedFavorites;
    });
    console.log("indexi", index);
  };
  // Sort books by released date in descending order
  const sortBooksByReleasedDate = (booksArray) => {
    return [...booksArray].sort((a, b) => {
      return new Date(a.released) - new Date(b.released);
    });
  };

 

  console.log(favorites, "favorites");
  return (
    <div className="App">
      <h1>Game of Thrones Books</h1>

      <h2>Fetch a list from an API and display it</h2>

      <button className="fetch-button" onClick={fetchBooks} disabled={loading}>
        {/* Conditional rendering based on "loading" state */}
        {loading ? (
          // Display a spinner when loading
          <div className="spinner"></div>
        ) : progress === 100 ? (
          // Display "Books" when progress is 100%
          "Books"
        ) : (
          // Display "Fetch Data" otherwise
          "Fetch Data"
        )}
        {/* Display progress percentage */}
        <div className="percentage">{progress.toFixed(0)}%</div>
      </button>
      <br />

      <div className="button-container">
        {/* Toggle "showFavorites" state */}
        <button
          className="buttons"
          onClick={() => setShowFavorites(!showFavorites)}
        >
          {showFavorites ? "Show Fetched Books" : "Show Favorites"}
        </button>

        {/* Conditional rendering of "Sort" button based on state */}
        {showSortComponent && !sortbutton && (
          <button
            className="buttons"
            onClick={() => setSortButton(!sortbutton)}
          >
            Sort
          </button>
        )}
      </div>

      {/* Conditional rendering of favorite books */}
      {showFavorites && (
        <div className="grid-container">
          {/* Map and render favorite books using "FavoriteBook" component */}
          {favorites.map((favoriteBook, index) => (
            <div key={favoriteBook.id}>
              <FavoriteBook
                book={favoriteBook}
                nr={index + 1}
                removeFromFavorites={removeFromFavorites}
                
              />
            </div>
          ))}
        </div>
      )}

      {/* Conditional rendering of sorted or unsorted books */}
      {showSortComponent && !showFavorites && (
        <div className="grid-container">
          {sortbutton ? (
            <div className="grid-container">
              <SortBookCards books={sortedBooks} />
            </div>
          ) : (
            <div className="grid-container">
              {books.map((book, index) => (
                <BookCard
                  addToFavorites={addToFavorites}
                  book={book}
                  key={book.id}
                  nr={index + 1}
                />
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}
export default App;

