import React from "react";
import { GoStar, GoStarFill } from "react-icons/go";
import { useState } from "react";

function BookCard({ book, nr, addToFavorites }) {
  const data = new Date(book.released).toDateString();

  const [clicekdinFavorite, setClicked] = useState(false);

  // Function to handle adding the book to favorites
  const handleAddToFavorites = () => {
    addToFavorites(book); // Add the current book to the favorites list
    setClicked(true);
  };

  return (
    //render book details
    <div className="book">
      <h3>Book: {nr}</h3>
      <h2>{book.name}</h2>

      <div className="details">
        <p>üë®: {book.authors}</p>
        <p>üìñ: {book.numberOfPages} pages</p>
        <p>üèòÔ∏è: {book.country}</p>
        <p>‚è∞: {data}</p>

        {/* Based in the state of clicekdinFavorite we change we change the text an icons */}
        <button onClick={handleAddToFavorites} className="star">
          {clicekdinFavorite ? (
            <>
              <GoStarFill /> Added
            </>
          ) : (
            <>
              <GoStar /> Add to Favorites
            </>
          )}
        </button>
      </div>
    </div>
  );
}

export default BookCard;
